from itertools import *
def threeSumClosest(nums, target):
	print(len(nums))
	com = combinations(sorted(nums),3)
	#print(map(lambda x :abs(sum(x)-target),com))
	return sum(min(imap(lambda x:[abs(sum(x)-target),x],combinations(nums,3)))[1])

